import React, {Component, Fragment} from 'react';
import { create } from 'istanbul-reports';


class OptionCTA extends Component {

    constructor(props){
        super(props);
        this.state={
            json: this.props.jsonOption,
            ctaNum : 0,
            ctaContent: '',
            isDropdown: false
        }
    }

    addingCta(e){
        e.preventDefault();
        let ctaCurrNum = this.state.ctaNum;
        function create(ctaCurrNum){
            let inx = ctaCurrNum + 1;
            let linkInputs =
                <div key={inx}><label key={"key_Text"+inx} htmlFor={"text-input-ctaText_"+inx}>Text:<input data-instancename="text" id={"text-input-ctaText_"+inx} name="text" placeholder={'text'} type="text" defaultValue={'text'}/></label><label key={"key__href"+inx} htmlFor={"text-input-ctaLink_"+inx}>Link:<input data-instancename="href" id={"text-input-ctaLink_"+inx} name="href" placeholder={'link'} type="text" defaultValue={'link'} /></label></div>;
              }

        // let linkInputs = this.state.json.map((item, index) => {
        //     return (
        //     <div key={index}><label key={"key_Text"+index} htmlFor={"text-input-ctaText_"+index}>Text:<input data-instancename="text" id={"text-input-ctaText_"+index} name="text" placeholder={item.text} type="text" defaultValue={item.text}/></label><label key={"key__href"+index} htmlFor={"text-input-ctaLink_"+index}>Link:<input data-instancename="href" id={"text-input-ctaLink_"+index} name="href" placeholder={item.href} type="text" defaultValue={item.href} /></label></div>);
        //   });
       this.setState({
        ctaNum : (ctaCurrNum === 4 ) ? ctaCurrNum : ctaCurrNum + 1,
        ctaContent: ''
       })
    }
    removingCta(e){
        let ctaCurrNum = this.state.ctaNum;
        e.preventDefault();
       this.setState({
        ctaNum : (ctaCurrNum === 0 ) ? ctaCurrNum : ctaCurrNum - 1
       })
    }
    dropdownSelected(e){
        console.dir(e.target.value);
        this.setState({
            isDropdown: !this.state.isDropdown 
        })
    }
render(){
    return(
        <Fragment>
            <div className="addCta" style={{"visibility":`${this.props.visibility}`}}>
                <input type="checkbox" defaultValue={this.state.isDropdown} onChange={this.dropdownSelected.bind(this)} /><span>Dropdown</span>
                <button onClick={this.addingCta.bind(this)}>add cta</button>
                <button onClick={this.removingCta.bind(this)}>remove cta</button>
            </div>
        </Fragment>
    );
};

};

export default OptionCTA;
