import React, {Component, Fragment} from 'react';


class OptionCTA extends Component {

    constructor(props){
        super(props);
        this.state={
            json: this.props.jsonOption,
            ctaNum : 0,
            ctaContent: ''
        }
    }

    addingCta(e){
        e.preventDefault();
        let ctaCurrNum = this.state.ctaNum;
        let linkInputs = this.state.json.map((item, index) => {
            return (
            <div key={index}><label key={"key_Text"+index} htmlFor={"text-input-ctaText_"+index}>Text:<input data-instancename="text" id={"text-input-ctaText_"+index} name="text" placeholder={item.text} type="text" defaultValue={item.text}/></label><label key={"key__href"+index} htmlFor={"text-input-ctaLink_"+index}>Link:<input data-instancename="href" id={"text-input-ctaLink_"+index} name="href" placeholder={item.href} type="text" defaultValue={item.href} /></label></div>);
          });
       this.setState({
        ctaNum : ctaCurrNum + 1,
        ctaContent: ''
       })
    }
    removingCta(e){
        let ctaCurrNum = this.state.ctaNum;
        e.preventDefault();
       this.setState({
        ctaNum : (ctaCurrNum === 0 ) ? ctaCurrNum : ctaCurrNum - 1
       })
    }

render(){
    return(
        <Fragment>
            <div className="addCta">
                <button onClick={this.addingCta.bind(this)}>add cta</button>
                <button onClick={this.removingCta.bind(this)}>remove cta</button>
            </div>
        </Fragment>
    );
};

};

export default OptionCTA;
