import React, { Component, Fragment} from 'react';

class ImgPreview extends Component {
    constructor(props){
      super(props);
      this.state={
        imgData: this.props.imgData,
        linksText: this.prop,
        elemChange:this.props.elemChange,
        jsonValue : this.props.jsonValue,
        isChecked : this.props,
        brandUpdate: '',
        imgUrl: 'https://www.gol.wip.gidapps.com'
      };
      this.brandStateUrl = this.brandStateUrl.bind(this);
    }

    brandStateUrl(nextProp){
      console.log(nextProp);

      // switch(this.props.brandName){
      //   case 'Gap':
      //     this.setState({
      //       imgUrl: `https://www.gol.wip.gidapps.com`
      //     })
      //   break;
      //   case 'Old Navy':
      //     this.setState({
      //       imgUrl: `https://www.onol.wip.gidapps.com`
      //     })
      //   break;
      //   case 'Banana Republic':
      //     this.setState({
