import React, { Component } from 'react';
import jsonData from './marketing_json/marketing-gap.json';
// first we will make a new context
const DefaultValue = {};
const MyContext = React.createContext(DefaultValue);

// Then create a provider Component
class MyProvider extends Component {
  state = {
    name: 'Wes',
    age: 100,
    cool: true,
    data: ''
  }
  render() {
    return (
      <MyContext.Provider value={this.state}>
        {this.props.children}
      </MyContext.Provider>
    )
  }
}

// const Family = (props) => (
//   <div className="family">
//     <Person />
//   </div>
// )

class Person extends Component {
  render() {
    return (
      <div className="person">
        <MyContext.Consumer>
          {(context) => (
            <React.Fragment>
              <p>state: {context.jsonData}</p>
              {/* <button onClick={context.growAYearOlder}>üç∞üç•üéÇ</button> */}
            </React.Fragment>
          )}
        </MyContext.Consumer>
      </div>
    )
  }
}


class FooContext extends Component {
  render() {
    return (
      <MyProvider>
        <div>
          <p>I am the FooContext</p>
        <Person></Person>
        </div>
      </MyProvider>
    );
  }
}


export default FooContext;
