import React, {Component, Fragment} from 'react';
import { create } from 'istanbul-reports';


class OptionCTA extends Component {

    constructor(props){
        super(props);
        this.state={
            json: this.props.jsonOption,
            ctaNum : 0,
            ctaContent: 'No Content Yet',
            isDropdown: false,
            ctaArr : 0,
            ctaCount : this.props.jsonOption.content.length,
        }
    }

    addingCta(e){
        // adding a duplicate of the array from this.props.jsonOption.content

        e.preventDefault();
        let ctaCurrNum = this.state.ctaNum + 1;
        const newArr = [];
         for(let i=0; i < this.state.ctaCount; i++){
            newArr.push([this.props.jsonOption.content[0]])
        };

       this.setState({
        ctaNum : (ctaCurrNum === 4 ) ? ctaCurrNum : ctaCurrNum + 1,

        json: newArr,
        ctaCount: this.state.ctaCount + 1
       })
       this.props.addCtaArr(this.state.ctaNum);





        // let linkInputs = this.state.json.map((item, ctaArr) => {
        //     return();
        // });

        // let linkInputs = this.state.json.map((item, index) => {
        //     return (
        //     <div key={index}><label key={"key_Text"+index} htmlFor={"text-input-ctaText_"+index}>Text:<input data-instancename="text" id={"text-input-ctaText_"+index} name="text" placeholder={item.text} type="text" defaultValue={item.text}/></label><label key={"key__href"+index} htmlFor={"text-input-ctaLink_"+index}>Link:<input data-instancename="href" id={"text-input-ctaLink_"+index} name="href" placeholder={item.href} type="text" defaultValue={item.href} /></label></div>);
        //   });
    }

    removingCta(e){
        let ctaCurrNum = this.state.ctaNum;
        const newArr = [this.props.jsonOption.content];
        e.preventDefault();
       this.setState({
        ctaNum : (ctaCurrNum === 0 ) ? ctaCurrNum : ctaCurrNum - 1,
        json: newArr.pop(),
        ctaCount: this.props.jsonOption.content.length
       })
       this.props.rmvCtaArr(this.state.ctaNum);
    }

    dropdownSelected(e){
        console.log(this.state.isDropdown );
        this.setState({
            isDropdown: !this.state.isDropdown 
        })
    }
render(){
    return(
        <Fragment>
            <div className="addCta" style={{"visibility":`${this.props.visibility}`}}>
                <input type="checkbox" defaultValue={this.state.isDropdown} onChange={this.dropdownSelected.bind(this)} /><span>Create Dropdown</span>
                <button onClick={this.addingCta.bind(this)}>add cta</button>
                <button onClick={this.removingCta.bind(this)}>remove cta</button>
                <div className="ctas">
                    {this.state.ctaContent}
                </div>
            </div>
        </Fragment>
    );
};

};

export default OptionCTA;
