import React, { Component, Fragment} from 'react';


class MobileToggle extends Component {
    constructor(props){
      super(props);
      this.state={
        isChecked: false,
        mobile: '',
        mobileSvg: '',
        desktop:'',
        svgDesktop:'',
        imgUrl: this.props.imgUrl
      };
      this.handleChange = this.handleChange.bind(this);
    }
    handleChange() {
        this.setState({ 
            isChecked: !this.state.isChecked,
            mobile: this.props.imgData.background.content.largeImg
        })

      }

        render(){
            console.log('mobile:', this.props);
            console.log('mobile state:', this.state.mobile);
            console.log('mobile url:', this.state.imgUrl);
            {if(this.state.isChecked){
            return(
                <Fragment>
                    <div className="switchWrapper">
                        <div>
                            <div className="onoffswitch">
                                <input type="checkbox" value={this.state.isChecked} id="mobileonOffSwitch" onChange={this.handleChange} name="onoffswitch" className="onoffswitch-checkbox"/>
                                <label className="onoffswitch-label" htmlFor="mobileonOffSwitch">   
                                    <span className="onoffswitch-inner"></span>
                                    <span className="onoffswitch-switch"></span>
                                </label>
                            </div>
                        </div>
                        <div>
                            <h6>Mobile Image Preview</h6>
                        </div>
                        <div style={{"display":"block"}}>
                            <img src={this.state.mobile} alt="mobile preview" />
                        </div>
                    </div>
                </Fragment>
            );
            }else{
            return(
                <Fragment>
                    <div className="switchWrapper">
                        <div>
                            <div className="onoffswitch">
                                <input type="checkbox" value={this.state.isChecked} id="mobileonOffSwitch" onChange={this.handleChange} name="onoffswitch" className="onoffswitch-checkbox"/>
                                <label className="onoffswitch-label" htmlFor="mobileonOffSwitch">   
                                    <span className="onoffswitch-inner"></span>
                                    <span className="onoffswitch-switch"></span>
                                </label>
                            </div>
                        </div>
                        <div>
                            <h6>Mobile Image Preview</h6>
                        </div>
                    </div>
                </Fragment>
            );
            }
        }
    }
}
export default MobileToggle;
